mapper.select:
   	SELECT seq,id, USERNAME,USERPASSWORD, phone, USEREMAIL, ROLE, delflag
	FROM USER 
	WHERE ID=#{id} AND USERPASSWORD=#{userpassword}

SELECT  id, USERNAME, phone, USEREMAIL, ROLE, delflag
FROM USER 
WHERE ID=#{id}

mapper.update:
UPDATE user
SET phone=#{phone}, USEREMAIL=#{useremail}
WHERE id=#{id};
